import type {SidebarsConfig} from '@docusaurus/plugin-content-docs';

// Import generated sidebar structure from sync-content.py
// Falls back to autogenerated if sync hasn't run yet
let tutorialItems: SidebarsConfig[string];
try {
  tutorialItems = require('./sidebar-generated.json');
} catch {
  tutorialItems = [{type: 'autogenerated', dirName: 'tutorials'}];
}

const sidebars: SidebarsConfig = {
  tutorialsSidebar: [
    {
      type: 'doc',
      id: 'index',
      label: 'Home',
    },
    {
      type: 'category',
      label: 'Tutorials',
      collapsed: false,
      link: {type: 'doc', id: 'tutorials/index'},
      items: tutorialItems as any[],
    },
  ],
};

export default sidebars;
